package array;

public class Example4_with_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String cars [] = { "volvo", "bmw","audi","ford","suzuki","jeep","maruthi","opel"};
		
		for ( int x = 0; x <= 8; x++)
			
			// 8 is the length of the array //
		{
			System.out.println(cars[x]);
		}
		
	}

}
