package java_conditions_statements;

public class Else_If_Statement1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int aGrade = 75;
	    int bGrade = 50;
	    int cGrade = 25;
	    
	    if (aGrade == cGrade)
	    {
	    System.out.println("Excellent");
	    }
	    else if (bGrade < aGrade)
	    {
	    System.out.println("Good");
	    }
	    else
	    {
	    System.out.println("Average");
	    }
	  }
	}
		